import React from 'react'; 

const About = () => {
  return (
    <about >
        <div className="bg-white text-gray-900 py-20 px-8 md:px-24 lg:px-32 xl:px-40 2xl:px-60">
            <div className="max-w-4xl mx-auto">
                <h2 className="text-4xl md:text-6xl font-bold mb-4">About Me</h2>
                <p className="text-lg md:text-xl mb-8">I'm a passionate and creative frontend developer with a focus on building user-friendly and responsive web applications. With a background in design, I bring a unique blend of skills that allows me to create visually appealing interfaces that are also highly functional.</p>
                <p className="text-lg md:text-xl mb-8">I have experience working with modern frontend technologies such as React, Vue.js, and Angular, as well as proficiency in HTML, CSS, and JavaScript. I enjoy learning new technologies and techniques to stay current in the ever-evolving field of web development.</p>
            </div>
            <a href="#portfolio" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full inline-block">View Resume</a>
        </div>    
    </about>
  );
};

export default About;
