import React, { useState } from 'react'; 
import resume from '../../resources/resume.pdf';
import skate from '../../resources/skateboard.png';

const About = () => {
  const [showModal, setShowModal] = useState(false);

  return (
    <div>
      <div className="bg-white text-gray-900 py-20 px-8 md:px-24 lg:px-32 xl:px-40 2xl:px-60">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-4xl md:text-6xl font-bold mb-4">About Me</h2>
          <p className="text-lg md:text-xl mb-8">I am currently a fourth-year student studying Nanotechnology Engineering with a minor in computing at the University of Waterloo. In my current role, I am an AI fullstack engineer at Pienso, an AI firm that helps turn data into insights, where I work on fullstack solutions alongside industry-standard applications of machine learning.</p>
          <p className="text-lg md:text-xl mb-8">I'm driven by projects that focus on the consumer and bring something new to the table. I'm open to new grad roles in 2025.</p>
        </div>
        <button
          onClick={() => setShowModal(true)}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full inline-block mt-4"
        >
          View Resume
        </button>
      </div>
      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none">
          <div className="relative w-auto max-w-3xl mx-auto my-6">
            {/* <img src={skate} alt="Resume" className="w-full" /> */}
            <iframe src={resume} className="w-full" style={"width: 100%"}/>
            <button
              onClick={() => setShowModal(false)}
              className="absolute top-0 right-0 m-4 text-gray-700 hover:text-gray-900"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default About;
