import React from 'react'; 

const Intro = () => {
  return (
    <intro >
        <div className="bg-white text-gray-900 py-20 px-8 md:px-24 lg:px-32 xl:px-40 2xl:px-60 flex flex-col items-center justify-center">
            <div className="max-w-4xl text-center">
                <h1 className="text-4xl md:text-6xl font-bold mb-4">Your Name</h1>
                <p className="text-lg md:text-xl mb-8">Frontend Developer | UI/UX Designer</p>
                <p className="text-lg md:text-xl mb-8">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis viverra ante nec tellus posuere, in tincidunt metus finibus. Vivamus ut eros consequat, eleifend urna eget, fermentum ligula. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas.</p>
                <a href="#portfolio" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full inline-block">View Portfolio</a>
            </div>
        </div>
    </intro>
  );
};

export default Intro;
