import React, { useState, useRef } from 'react';
import resume from '../../resources/resume.jpg';

const Header = ({ projectsRef, aboutRef }) => {
  const [showModal, setShowModal] = useState(false);

  const scrollToProjects = () => {
    projectsRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToAbout = () => {
    aboutRef.current.scrollIntoView({ behavior: 'smooth' });
  };


  return (
    <header className="header">
      <nav className="bg-white border-gray-200 dark:bg-gray-900">
        <div className="max-w-screen-xl flex items-center justify-between mx-auto p-4">
          <div className="hidden md:block md:w-auto ml-auto" id="navbar-default">
            <ul className="font-medium flex flex-col p-4 md:p-0 mt-4 border border-gray-100 rounded-lg bg-gray-50 md:flex-row md:space-x-8 rtl:space-x-reverse md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
              <li>
                <a onClick={() => scrollToProjects(projectsRef)} className="block py-2 px-3 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-white md:dark:hover:text-blue-500 dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent">Projects</a>
              </li>
              <li>
                <a onClick={() => scrollToAbout(aboutRef)} className="block py-2 px-3 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-white md:dark:hover:text-blue-500 dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent">About</a>
              </li>
              <li>
                <a onClick={() => setShowModal(true)} className="block py-2 px-3 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-white md:dark:hover:text-blue-500 dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent">Resume</a>
              </li>
            </ul>
          </div>
        </div>
      </nav>    
      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none">
          <div className="fixed inset-0 bg-gray-900 opacity-50"></div>
          <div className="relative w-auto max-w-3xl mx-auto my-6 bg-white rounded-lg shadow-lg">
            <img src={resume} alt="Resume" className="w-full" />
            <button
              onClick={() => setShowModal(false)}
              className="absolute top-0 right-0 m-4 text-gray-700 hover:text-gray-900"
            >
              X
            </button>
          </div>
        </div>
      )}  
    </header>
  );
};

export default Header;
