import React from 'react'; 

const About = () => {
  return (
    <about >
        <div className="bg-white text-gray-900 py-20 px-8 md:px-24 lg:px-32 xl:px-40 2xl:px-60">
            <div className="max-w-4xl mx-auto">
                <h2 className="text-4xl md:text-6xl font-bold mb-4">About Me</h2>
                <p className="text-lg md:text-xl mb-8">I am currently a fourth-year student studying Nanotechnology Engineering with a minor in computing at the University of Waterloo. In my current role I am a AI fullstack engineer at Pienso, an AI firm that helps turns data into insights, 
                where I work on fullstack solutions alongisde industry standard applications of machine learning. </p>
                <p className="text-lg md:text-xl mb-8">I'm driven by projects that focus on the consumer and bring something new to the table. I'm open to new grad roles in 2025</p>
            </div>
            <a href="https://docs.google.com/document/d/1widLv9D-eYUEc8kFt7YRQl7VRPPA_D-E/edit?usp=sharing&ouid=112759744359565282163&rtpof=true&sd=true" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full inline-block">View Resume</a>
        </div>    
    </about>
  );
};

export default About;
